import React, { useState } from "react";
import "./ExpenseForm.css";

export default function ExpenseForm(props) {
  const [enteredTitle, setEnteredTitle] = useState("");
  const [enteredAmount, setEnteredAmount] = useState("");
  const [enteredDate, setEnteredDate] = useState("");
  const [box, setBox] = useState(true)

  const inputChangeHandler = (i, value) => {
    if (i === "title") {
      setEnteredTitle(value);
    } else if (i === "date") {
      setEnteredDate(value);
    } else {
      setEnteredAmount(value);
    }
  };

  const submitHandler = (e) => {
    e.preventDefault();

    const expenseData = {
      title: enteredTitle,
      amount: enteredAmount,
      date: new Date(enteredDate),
    };

    props.onSaveExpenseData(expenseData);
    setEnteredAmount("");
    setEnteredDate("");
    setEnteredTitle("");
  };

  const expenseForm = <div>
    <div className="new-expense__controls">
    <div className="new-expense__control">
      <label>Title</label>
      <input
        value={enteredTitle}
        type="text"
        onChange={(e) => {
          inputChangeHandler("title", e.target.value);
        }}
      />
    </div>
    <div className="new-expense__control">
      <label>Amount</label>
      <input
        value={enteredAmount}
        onChange={(e) => {
          inputChangeHandler("amount", e.target.value);
        }}
        type="number"
        min="0.01"
        step="0.01"
      />
    </div>
    <div className="new-expense__control">
      <label>Date</label>
      <input
        value={enteredDate}
        onChange={(e) => {
          inputChangeHandler("date", e.target.value);
        }}
        type="date"
        min="2019-01-01"
        max="2022-12-31"
      />
    </div>
  </div>
  <div className="new-expense__actions">
  <button type="submit">Cancle</button> <button type="submit">Add Expense</button>
  </div>
    </div>


const newForm = <form>test</form>

  return (
    <form onSubmit={submitHandler}>
      {expenseForm}
    </form>

  );
}
